    "proxyPort": {                                    "en": "Proxy-Port",                                       "de": "Proxy-Port",                                       "ru": "Порт прокси",                                      "pt": "Porta proxy",                                      "nl": "Proxy-Port",                                       "fr": "Port proxy",                                       "it": "Porta proxy",                                      "es": "Puerto proxy",                                     "pl": "Port proxy",                                       "uk": "Проксі-порт",                                      "zh-cn": "代理端口"},
